'use strict';

var headless_error_client = require('../../error/client.js');
var headless_wasm_create = require('../../wasm/create.js');
require('../../wasm/instantiate.js');

const wasmGetSignHandler = async wasmUrl => {
  const instance = await headless_wasm_create.createWasmInstance(wasmUrl);
  try {
    const signMessageHandler = await instance.signMessage();
    if (signMessageHandler) {
      return signMessageHandler;
    }
  } catch (_) {
    /* empty */
  }
  throw new headless_error_client.HeadlessClientError({
    cause: undefined,
    code: headless_error_client.HeadlessClientErrorCode.HandlerNotFoundError,
    message: `Unable to get the sign handler. This could be due to a wrong version of WASM with url"${wasmUrl}"`
  });
};

exports.wasmGetSignHandler = wasmGetSignHandler;
