'use strict';

var headless_action_sendTransaction_common = require('../send-transaction/common.js');

const isEIP1559CompatibleTransaction = type => type === headless_action_sendTransaction_common.SupportedTransaction.EIP1559 || type === headless_action_sendTransaction_common.SupportedTransaction.RoninGasSponsor;
const isRoninGasSponsorTransaction = type => type === headless_action_sendTransaction_common.SupportedTransaction.RoninGasSponsor;
const isRoninLegacyTransaction = type => type === headless_action_sendTransaction_common.SupportedTransaction.Legacy;
const isSupportedTransaction = type => type !== undefined && Object.values(headless_action_sendTransaction_common.SupportedTransaction).includes(type);

exports.isEIP1559CompatibleTransaction = isEIP1559CompatibleTransaction;
exports.isRoninGasSponsorTransaction = isRoninGasSponsorTransaction;
exports.isRoninLegacyTransaction = isRoninLegacyTransaction;
exports.isSupportedTransaction = isSupportedTransaction;
