'use strict';

var isAddress = require('./isAddress-Ajbo9xO8.js');
var keccak256 = require('./keccak256-DkMFbAQi.js');

/**
 * @description Converts an ECDSA public key to an address.
 *
 * @param publicKey The public key to convert.
 *
 * @returns The address.
 */
function publicKeyToAddress(publicKey) {
  const address = keccak256.keccak256(`0x${publicKey.substring(4)}`).substring(26);
  return isAddress.checksumAddress(`0x${address}`);
}

exports.publicKeyToAddress = publicKeyToAddress;
